document.getElementById('file').onchange = function(){ // Save File Loaded
    var file = this.files[0];
    var reader = new FileReader();
    reader.onload = function(progressEvent){
        if (this.result[0] == "{") {
            saveJson = JSON.parse(this.result);
        } else {
            saveJson = JSON.parse(this.result.split('\n')[3]);
        }
        change();
    };
    reader.readAsText(file);
};

function saveToFile() { // Save a checklist
    var myBlob = new Blob([JSON.stringify(saveJson)], {type: 'text/json'});
    var anchor = document.createElement("a");
    anchor.download = "NOT A SAVEFILE.json";
    anchor.href = window.URL.createObjectURL(myBlob);
    anchor.click();
}

function checkMarkerType(elem, marker, name, imBase) {
    if (elem.hasOwnProperty("npc")) {
        marker.src = imBase + "NPC.png";
        return marker, name + " (from NPC)";
    } else if (elem.hasOwnProperty("treasure")) {
        marker.src = imBase + "Treasure.png";
        return marker, name + " (Treasure Hunters)"
    } else {
        marker.src = imBase + ".png"
        return marker, name
    }
}

function change() { // Place markers and list elements
    var name = "";
    var markers = document.getElementById("litterMarkers"); // Our markers parent element
    var marker = document.createElement("img"); // Create marker
    marker.style.position = "absolute";
    marker.style.height = "30px";
    markers.innerHTML = "";
    list = document.getElementById("litterListDiv"); // LITTER - get list
    list.innerHTML = "" // Clear list
    for (const key in litter) { // For each of the keys in litter
        var screen = litter[key]["screen"].split("_");
        var hasPropertyUnbypass = false;
        if (litter[key].hasOwnProperty("require")) {
            if (saveJson.hasOwnProperty(litter[key]["require"]["key"])) {
                var dataValue = saveJson[litter[key]["require"]["key"]]
                var targetValue = litter[key]["require"]["value"]
                if ({"=": dataValue==targetValue,">": dataValue>targetValue,"<": dataValue<targetValue}[litter[key]["require"]["operation"]]) {
                    hasPropertyUnbypass = true
                }
            }
        }
        if ((!saveJson.hasOwnProperty(key) && !hasPropertyUnbypass) & screen[0] == currentMap) { // If the save data doesn't contain it and we're on the right layer
            marker, name = checkMarkerType(litter[key],marker,key,"assets/markers/litter") // Check for special markers (NPC, treasure guys)
            addMarker(key,litter[key],screen,markers,marker,list,0,name);
        }
    }
    var markers = document.getElementById("giftMarkers"); // GIFTS
    markers.innerHTML = "";
    var list = document.getElementById("giftListDiv");
    list.innerHTML = ""
    for (const key in gifts) {
        var screen = gifts[key]["screen"].split("_");
        if (!saveJson.hasOwnProperty(key) & screen[0] == currentMap) {
            marker, name = checkMarkerType(gifts[key],marker,gifts[key]["name"],"assets/markers/gifts")
            addMarker(key,gifts[key],screen,markers,marker,list,0,name);
        }
    }
    if (currentMap == 0) {
        var bowlOfOats = "";
        for (const key in oats) { // Oats' gifts
            if (!saveJson.hasOwnProperty(key)) {
                if (bowlOfOats == "") {bowlOfOats = oats[key]}
                else {bowlOfOats = bowlOfOats + ", " + oats[key]}
            }
        }
        if (bowlOfOats != "") {
            bowlOfOats = bowlOfOats + " (from Oats)";
            marker.src = "assets/markers/giftsOats.png";
            addMarker("oatsgifts",oatsData,[0,0,2],markers,marker,list,0,bowlOfOats);
        }
    }
    var markers = document.getElementById("animalMarkers"); // ANIMALS
    marker.src = "assets/markers/animal.png";
    markers.innerHTML = "";
    var list = document.getElementById("animalListDiv");
    list.innerHTML = ""
    for (const key in animals) {
        var screen = animals[key]["screen"].split("_");
        if (!saveJson.hasOwnProperty(key) & screen[0] == currentMap) {
            addMarker(key,animals[key],screen,markers,marker,list,-23);
        }
    }
    var markers = document.getElementById("entranceMarkers"); // ENTRANCES
    marker.src = "assets/markers/entrances/dot.png";
    markers.innerHTML = "";
    for (const key in entrances) {
        var screen = entrances[key]["screen"].split("_");
        if (!saveJson.hasOwnProperty(key) & screen[0] == currentMap) {
            marker.src = "assets/markers/entrances/" + entranceMarkerNames[entrances[key]["direction"]]; // Set the image to the correct direction arrow
            if (entrances[key]["to"]) {var entranceTo = entrances[key]["to"]} else {var entranceTo = "none"}
            marker.setAttribute("entranceTo",entranceTo); // Set our entranceTo value so we know where to go when we click
            addMarker(key,entrances[key],screen,markers,marker,null,entranceOffsets[entrances[key]["direction"]],null,false,goToEntrance);
        }
    }
    var extrasListStuff = document.getElementById("extrasListStuff");
    extrasListStuff.innerHTML = "";
    var br = document.createElement("br");
    for (const key in extras) {
        console.log(key)
        var hasPropertyBypass = false;
        if (extras[key].hasOwnProperty("require")) {
            if (saveJson.hasOwnProperty(extras[key]["require"]["key"])) {
                var dataValue = saveJson[extras[key]["require"]["key"]]
                var targetValue = extras[key]["require"]["value"]
                if ({"=": dataValue==targetValue,">": dataValue>targetValue,"<": dataValue<targetValue}[extras[key]["require"]["operation"]]) {
                    hasPropertyBypass = true
                }
            }
        }
        if (!saveJson.hasOwnProperty(key) || hasPropertyBypass) {
            extrasListStuff.innerHTML = extrasListStuff.innerHTML + extras[key]["text"];
            extrasListStuff.appendChild(br)
        }
    }
}

function toggleMarkerButton(id) { // Button for toggling markers
    var toggleButton = document.getElementById(id + "Button");
    if (toggleButton.innerHTML != "O") {
        toggleButton.innerHTML = "O";
    } else {
        toggleButton.innerHTML = "U";
    }
    toggleMarkers(id);
}

function toggleMarkers(id) { // Actually toggles markers
    var markers = document.getElementById(id);
    if (markers.style.visibility != "hidden") {
        markers.style.visibility = "hidden";
    } else {
        markers.style.visibility = "visible";
    }
}

function goToEntrance(e) { // Go to elements target entrance
    if (e.target.getAttribute("entranceTo")=="none") {return;}
    var view = document.getElementById("mapview");
    var targetEntrance = entrances[e.target.getAttribute("entranceTo")];
    var screen = targetEntrance["screen"].split("_");
    if (Number(screen[0]) != currentMap) {
        changeLayer(Number(screen[0]));
    }
    var target = document.getElementById(e.target.getAttribute("entranceTo"));
    selectMarker(target,false,true);
    centerToSelection();
}

function addMarker(key,locationData,screen,markers,marker,list,yDiff=0,name=null,dolist=true,onclick=selectMarker) { // Add a marker
    if (!name) {name = key}
    var posX = mapCentres[currentMap][currentMapSize][0] + (1920/mapGridDivs[currentMapSize]*Number(screen[1])) + locationData["x"]/mapGridDivs[currentMapSize];
    var posY = mapCentres[currentMap][currentMapSize][1] + (1080/mapGridDivs[currentMapSize]*Number(screen[2])) + locationData["y"]/mapGridDivs[currentMapSize]+yDiff;
    marker.style.left = posX-15 + "px";
    marker.style.top = posY-15 + "px";
    marker.id = key;
    marker.setAttribute("markerPosX", posX);
    marker.setAttribute("markerPosY", posY);
    marker.title = name.replaceAll("found_litter_","").replaceAll("gift_","").replaceAll("found_animal_","").replaceAll("_"," ");
    var tempMark = marker.cloneNode()
    tempMark.addEventListener("click",onclick);
    markers.appendChild(tempMark);
    if (dolist) {
        var listElem = document.createElement("a");
        listElem.href = "#";
        listElem.setAttribute("markerPosX", posX);
        listElem.setAttribute("markerPosY", posY);
        listElem.setAttribute("markerId", key);
        var deleteButton = document.createElement("a");
        deleteButton.classList.add("deleteButton")
        deleteButton.innerHTML = "X";
        deleteButton.href = "#";
        list.appendChild(deleteButton)
        deleteButton.addEventListener("click",removeList)
        listElem.innerHTML += " " + marker.title + "<br>";
        list.appendChild(listElem);
        listElem.addEventListener("click",function(e) {
            var view = document.getElementById("mapview");
            if (window.matchMedia("(orientation: portrait)").matches) { // If we're in portrait (so have tabs) close the tab
                t = e.target.parentNode.parentNode
                selectTabB(t.previousSibling.previousSibling,t)
            }
            sb.scrollTo({x: e.target.getAttribute("markerPosX")-view.offsetWidth/2, y: e.target.getAttribute("markerPosY")-view.offsetHeight/2}); // Scroll to marker
            selectMarker(e,true)
        })
    }
}

function removeList(e) { // Removes marker, its list element and adds it to the 'save'
    document.getElementById(e.target.nextSibling.getAttribute("markerId")).remove()
    saveJson[e.target.nextSibling.getAttribute("markerId")] = 1.0
    e.target.nextSibling.remove() // Actual list element
    e.target.remove(); // X button
}

function centerToSelection() { // Centres the map on the selected marker
    var view = document.getElementById("mapview");
    var selection = document.getElementsByClassName("selectedMarker")[0]
    sb.scrollTo({x: selection.getAttribute("markerPosX")-view.offsetWidth/2, y: selection.getAttribute("markerPosY")-view.offsetHeight/2});
}

function changeLayer(layer) { // Changes the maps layer 
    if (currentMap == layer) {toCenter(true);return} // why doe
    currentMap = layer;
    var mapImg = document.getElementById("mapImg");
    mapImg.src = "";
    document.getElementById("loadingpos").classList.remove("loadingvisible");
    mapImg.src = "assets/maps/"+layer+"/"+currentMapSize+".png";
    change();
}

function toCenter(noscroll) { // Centres the map
    const viewport = document.getElementById("mapview");
    posTo = {x: mapCentres[currentMap][currentMapSize][0]-viewport.offsetWidth/2+(1920/mapGridDivs[currentMapSize])/2,y: mapCentres[currentMap][currentMapSize][1]-viewport.offsetHeight/2+(1080/mapGridDivs[currentMapSize])/2}
    if (noscroll) {
        sb.setPosition(posTo);  
    } else {
        sb.scrollTo(posTo);
    }
}

function selectMarker(e,t=false,g=false) { // Selects a marker, if t assume we're in a list element and select our markerId, if g, e is the element and not e.target | Sorry for the dogshit variable names
    if (g) {
        var target = e
    } else {
        var target = e.target;
    }
    if (t) {
        target = document.getElementById(e.target.getAttribute("markerId"));
    }
    var selMarkers = document.getElementsByClassName("selectedMarker");
    var desel = false;
    for (let i = 0;i<selMarkers.length;i++) {
        if (selMarkers[i].id == target.id) { // If the one we're selecting is already selected, set desel to true to not add class after
            desel = true;
        }
        selMarkers[i].classList.remove("selectedMarker");
    }
    if (!desel) {
        target.classList.add("selectedMarker");
    }
}
function makeMap() { // This makes the map scrollable in the first place
    const map = document.getElementById("map");
    const viewport = document.getElementById("mapview")

    sb = new ScrollBooster({
        viewport,
        map,
        scrollMode: 'transform',
        emulateScroll: false,
        textSelection: true,
    });
    sb.setPosition({
      x: mapCentres[currentMap][currentMapSize][0]-viewport.offsetWidth/2+(1920/mapGridDivs[currentMapSize])/2,
      y: mapCentres[currentMap][currentMapSize][1]-viewport.offsetHeight/2+(1080/mapGridDivs[currentMapSize])/2
    });
    change();
}

document.getElementById('map').onclick = function clickEvent(e) { // This is for getting the clicked position on the map
    var rect = e.target.getBoundingClientRect();
    var x = e.clientX - rect.left; //x position within the element.
    var y = e.clientY - rect.top;  //y position within the element.
    var screen = []
    var screenPos = []
    screen[0] = currentMap
    screen[1] = (x-mapCentres[currentMap][currentMapSize][0])/(1920/mapGridDivs[currentMapSize])
    screenPos[0] = 1920 * (screen[1] - Math.floor(screen[1]))
    screen[1] = Math.floor(screen[1])
    screen[2] = (y-mapCentres[currentMap][currentMapSize][1])/(1080/mapGridDivs[currentMapSize])
    screenPos[1] = 1080 * (screen[2] - Math.floor(screen[2]))
    screen[2] = Math.floor(screen[2])
    document.getElementById("clickedPos").innerHTML = "Last Clicked - Screen: L " + screen[0] + " X " + screen[1] + " Y " + screen[2] + " Pos: X " + Math.round(screenPos[0]*100)/100 + " Y " + Math.round(screenPos[1]*100)/100;
    if (copyDebugStuffToClipboard) {
        navigator.clipboard.writeText(',\n"savethinggoeshere": {\n    "screen": "'+screen[0]+"_"+screen[1]+"_"+screen[2]+'",\n    "x": '+screenPos[0]+',\n    "y": '+screenPos[1]+',\n    "npc": true\n}'); // THIS IS NOW for adding npc litter to the thing!! awesome!
    }
}

