function selectTab(e) { // This selects the tab
    const selected = document.getElementsByClassName("tabSelected");
    if (selected.length > 1) { // If a tab is already selected
        t = selected[0]
        selectTabB(selected[0],selected[1]); // Deselect tab
        if (t.id == e.target.id) {return} // If we were already selected we don't wanna reselected ourself
    }
    const target = e.target;
    const target2 = e.target.nextSibling.nextSibling;
    selectTabB(target,target2) // Select us!
}

function selectTabB(target,target2) { // This controls adding/removing the class, target is the tab and target2 is the list usually
    if (!target.classList.contains("tabSelected")) { // If we aren't selected
        if (target.classList.contains("tabDeselected")) { // If we have deselected class, remove it
            target.classList.remove("tabDeselected")
            target2.classList.remove("tabDeselected")
        }
        target.classList.add("tabSelected") // Add selected class
        target2.classList.add("tabSelected")
    } else { // If we are selected
        target.classList.remove("tabSelected") // Remove selected class
        target2.classList.remove("tabSelected")
        target.classList.add("tabDeselected") // Add deselected class
        target2.classList.add("tabDeselected")
    }
}

var tabs = document.getElementsByClassName("tab")

for (let i = 0;i<tabs.length;i++) {
    tabs[i].addEventListener("click",selectTab); // Add selectTab event listener to tabs
}

